import React, { useState } from 'react';
import DataTable from 'react-data-table-component';
import { getTokenHolderList, getAddressTokenBalance } from './api';

const Record = (props) => {
  const [address, setAddress] = useState('');
  const [contractAddress, setContractAddress] = useState('');
  const [columns, setColumns] = useState([]);
  const [page, setPage] = useState(1);
  const [data, setData] = useState([]);
  const [errMsg, setErrMsg] = useState('');

  const searchTokenHolderList = async () => {
    try {
      const response = await getTokenHolderList(contractAddress, 1);
      if (response.status === '1') {
        setData(response.result);
        setColumns(tokenHolderListColumns);
        setPage(1);
        setErrMsg('');
      } else {
        setErrMsg(response.message);
      }
    } catch (err) {
      setErrMsg(err);
    }
  }

  const serachAddressTokenBalance = async () => {
    try {
      const response = await getAddressTokenBalance(address, 1);
      if (response.status === '1') {
        setData(response.result);
        setColumns(addressTokenBalance);
        setPage(1);
        setErrMsg('');
      } else {
        setErrMsg(response.message);
      }
    } catch (err) {
      setErrMsg(err);
    }
  }

  const addressTokenBalance = [
    {
      name: 'Token Address',
      selector: (row) => row.TokenAddress
    },
    {
      name: 'Token Name',
      selector: (row) => row.TokenName
    },
    {
      name: 'Token Symbol',
      selector: (row) => row.TokenSymbol
    },
    {
      name: 'Token Quantity',
      selector: (row) => row.TokenQuantity
    },
    {
      name: 'Token Divisor',
      selector: (row) => row.TokenDivisor
    }
  ];

  const tokenHolderListColumns = [
    {
      name: 'Token Holder Address',
      selector: (row) => row.TokenHolderAddress
    },
    {
      name: 'Token Holder Quantity',
      selector: (row) => row.TokenHolderQuantity
    }
  ];

  return (
    <div>
      <hr />
      <h1>Record</h1>
      <div>
        <label htmlFor='address'>Address: </label>
        <input id='address' onChange={ (e) => setAddress(e.target.value) } />
      </div>
      <div>
        <label htmlFor='contractAddress'>Contract Address: </label>
        <input id='contractAddress' onChange={ (e) => setContractAddress(e.target.value) }/>
      </div>
      <button onClick={serachAddressTokenBalance}>Search Address Token Balance</button>
      <button onClick={searchTokenHolderList}>Search Token Holder List</button>
      <div>{errMsg}</div>
      <DataTable
        columns={Object.keys(data[0]).map((key) => ({ name: key, selector: (row) => row[key] }))
}
        data={data}
      />
    </div>
  );
};

export default Record;
