import React, { useState } from 'react';
import DataTable from 'react-data-table-component';
import { getTokenHolderList } from './api';

const Record = (props) => {
  const [address, setAddress] = useState('');
  const [contractAddress, setContractAddress] = useState('');
  const [page, setPage] = useState(1);
  const [data, setData] = useState([]);
  const [errMsg, setErrMsg] = useState('');

  const searchTokenHolderList = async () => {
    try {
      const result = await getTokenHolderList(contractAddress, 1);
      setData(JSON.stringify(result));
      setPage(1);
    } catch (err) {
      setErrMsg(err);
    }
  }

  return (
    <div>
      <hr />
      <h1>Record</h1>
      <div>
        <label htmlFor='address'>Address: </label>
        <input id='address' onChange={ (e) => setAddress(e.target.value) } />
      </div>
      <div>
        <label htmlFor='contractAddress'>Contract Address: </label>
        <input id='contractAddress' onChange={ (e) => setContractAddress(e.target.value) }/>
      </div>
      <button onClick={searchTokenHolderList}>Search</button>
      <div>{errMsg}</div>
      <DataTable
        columns={columns}
        data={data}
      />
    </div>
  );
};

export default Record;
