export async function connectToBinance() {
  console.log("Connect Binance");
  //window.BinanceChain = new Web3('https://bsc-dataseed1.binance.org:443');

  try {
    await BinanceChain.enable();

    console.log(BinanceChain)

    hideWalletButtons();
    initJoinButton();

  } catch (err) {
    $('.status').html('User denied account access', err)
  }

  accounts = await BinanceChain.request({
    method: 'eth_requestAccounts'
  });
  const account = accounts[0];
  showAccount.innerHTML = account;

  connectedBinance = true;
  $('.status').html('Connected to BinanceWallet');
}
