import React from 'react';
import './App.css';
import ConnectWalletButtons from './components/ConnectWalletButtons';

function App() {
  if (typeof window.ethereum !== 'undefined') {
    console.log('MetaMask is installed!');
  } 
  if (typeof window.BinanceChain !== 'undefined') {
    console.log('BinanceWallet is installed!');
  }

  return (
    <div>
      <ConnectWalletButtons />

      <div id="bidPriceSlider"></div>

      <p>
        <label htmlFor="bidAmount">Bid price:</label>
        <input type="number" id="bidAmount" />
        <label htmlFor="bidAmount">BNB</label>

      </p>

      <button className="joinAuctionButton btn" disabled>Join Auction</button>


      <h2>Account: <span className="showAccount">Not Connected</span></h2>
      <h2>Status: <span className="status">Not Connected</span></h2>

    </div>
  );
}

export default App;
