import ConnectWalletButton from "./ConnectWalletButton";
import Web3 from 'web3';

const ConnectWalletButtons = (props) => {
  async function connectMetamask() {
    console.log("Connect Metamask");
    window.web3 = new Web3('https://bsc-dataseed1.binance.org:443');
    try {
      await window.ethereum.enable();

      console.log(window.ethereum)

      hideWalletButtons();
      initJoinButton();

    } catch (err) {
      $('.status').html('User denied account access', err)
    }

    accounts = await window.ethereum.request({
      method: 'eth_requestAccounts'
    });
    const account = accounts[0];
    showAccount.innerHTML = account;

    connectedMetamask = true;
    $('.status').html('Connected to Metamask');
  }

  async function connectBinance() {
    console.log("Connect Binance");
    //window.BinanceChain = new Web3('https://bsc-dataseed1.binance.org:443');

    try {
      await BinanceChain.enable();

      console.log(BinanceChain)

      hideWalletButtons();
      initJoinButton();

    } catch (err) {
      $('.status').html('User denied account access', err)
    }

    accounts = await BinanceChain.request({
      method: 'eth_requestAccounts'
    });
    const account = accounts[0];
    showAccount.innerHTML = account;

    connectedBinance = true;
    $('.status').html('Connected to BinanceWallet');
  }  

  return (
    <>
      <ConnectWalletButton name='Connect Metamask' onClick={connectMetamask} />
      <ConnectWalletButton name='Connect BinanceWallet' onClick={connectBinance} />
    </>
  );
};

export default ConnectWalletButtons;
